import { useState, useRef } from 'react';

import Button from '../UI/Button';
import Card from '../UI/Card';
import classes from './AddUser.module.css';
import ErrorModal from '../UI/ErrorModal';

const AddUser = (props) => {

	const enteredUserUsername = useRef();
	const enteredUserAge = useRef();

	const [enteredUsername, setEnteredUsername] = useState('');
	const [enteredAge, setEnteredAge] = useState('');
	const [error, setError] = useState();

	const handleAddUser = (e) => {
		e.preventDefault();
		if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
			setError({
				title: 'Invalid input',
				message: 'Please enter a valid name and age (non-empty values).'
			});
			return;
		}
		if (+enteredAge < 0) {
			setError({
				title: 'Invalid age',
				message: 'Please enter a valid age > 0'
			})
			return;
		}
		props.onAddUser(enteredUsername, enteredAge);
		setEnteredUsername('');
		setEnteredAge('');
	};

	const handleChangeUsernameInput = (e) => {
		setEnteredUsername(e.target.value);
	};

	const handleChangeAgeInput = (e) => {
		setEnteredAge(e.target.value);
	};

	const handleCloseModal = () => {
		setError();
	}

	return (
		<div>
			{error && <ErrorModal title={error.title} message={error.message} handleCloseModal={handleCloseModal} /> }
			<Card className={classes.input}>
				<form onSubmit={handleAddUser}>
					<label htmlFor='username'>Username</label>
					<input
						id='username'
						type='text'
						onChange={handleChangeUsernameInput}
						value={enteredUsername}

					/>
					<label htmlFor='age'>Age (Years)</label>
					<input
						type='number'
						id='age'
						onChange={handleChangeAgeInput}
						value={enteredAge}
						ref={enteredUserAge}
					/>
					<Button type='submit'>Add User</Button>
				</form>
			</Card>
		</div>
	);
};

export default AddUser;
